									modifier.$set.status = "Working in progress";
									checkCompleted();}


if(( modifier.$set.status !== "Archived" && doc.status === "Approved")) {
									modifier.$set.status = "Working in progress"; 
									checkCompleted();} 


if( modifier.$set.status === "Approved") {                 
  		// Inserisce il numero di audit automaticamente
			var max = 0;
            var auditNumbers = Activities.find({status: { "$in" : ["Approved","Archived"]}},{ fields: { number: 1 }}).fetch();
			_.each(auditNumbers, function(doc)	{var intNum = parseInt(doc.number.split('/')[0]);if(!isNaN(intNum) && intNum > max) max = intNum;});
			modifier.$set.number =  (max + 1)+"/"+moment().format('YYYY');
