var clientMonitoringdetectionByid = new Mongo.Collection('clientMonitoringdetectionByid');


// TIP&TRICK code moved in "Before Subscription Code" to limit subscription at this page.
//Meteor.subscribe("MonitoringdetectionByid");


Template.TEMPLATE_NAME.helpers({
  chartData: function(chart) {
		var x=clientMonitoringdetectionByid.findOne( { _id: chart._id } );
		if (!x) { var x = {}; x.keyIndicator = 'n/a'; x.date = '';}
        var res = {
               data: {
                       json: {
                               data: [x.keyIndicator]
                             },
                        type: chart.charttype						
                     },
				legend: { show: false },
				size: {height : 100}

				};

		switch (x.keyIndicator)  {
			case "OK":
				var YesNo = "fa fa-check fa-5x text-success";
				break;
			case "KO":
				var YesNo = "fa fa-close fa-5x text-danger";
				break;
          	case "NA":
            	var YesNo = "fa fa-ellipsis-h fa-5x text-warning";
            	break;
			}

        tag1="#";
		id1="id";
		id = id1.concat(chart._id);
		tag= tag1.concat(id);
		return { data: res, YesNo: YesNo, id: id , date: x.date, title: chart.title, control_description: chart.control_description, enabled: chart.enabled, tag: tag, color: chart.color };    
   },

  equals: function(a, b) {
		return a === b;
	}

});


