export class QUERY_LDAP extends Component {

	constructor() {
		super();
	}

	componentWillMount() {
		/*TEMPLATE_CREATED_CODE*/
	}

	componentWillUnmount() {
		/*TEMPLATE_DESTROYED_CODE*/
	}

	componentDidMount() {
		/*TEMPLATE_RENDERED_CODE*/

		Meteor.defer(function() {
			globalOnRendered();
		});
	}

	profili_all() {

				//console.log('Numero profili rilevati:',Profili.find().count());
				return Profili.find();

	}

	onClickUsers(e) {

				Meteor.call("ldapquery", {}, function(e, r) {
				    if(e) console.log("errore:",e); 
					console.log("query OK");
				});      


	}

	render() {
		return (
			<div>
				<h1>

							Lista users  Active Directory

				</h1>
				<div>
					<button className="btn btn-default" id="users" onClick={this.onClickUsers}>

									Aggiornamento users

					</button>
					{this.profili_all() ? (
						<table className="table table-striped">
							<thead>
								<tr>
									<th>





																																				User									





									</th>
									<th>





																																				Department





									</th>
									<th>





																																				When Changed									





									</th>
								</tr>
							</thead>
							<tbody>
								{
									profili_all.map(function(item, itemIndex) {
										return (
											<tr key={itemIndex}>
												<td>




																																						{item.user}					




												</td>
												<td>




																																                        {item.department}			




												</td>
												<td>




																																						{item.whenCreated}					




												</td>
											</tr>
										);
									})
								}
							</tbody>
						</table>
					) : (
						<h3>

											Nessun user :(

						</h3>
					)}
				</div>

			</div>
		);
	}
}

export const QUERY_LDAPContainer = createContainer(function(props) {
	/*SUBSCRIPTIONS*/
}, QUERY_LDAP);

