var pageSession = new ReactiveDict();

Template.TEMPLATE_NAME.onCreated(function() {
	pageSession.set("uploading", false );
});


Template.TEMPLATE_NAME.helpers({
  uploading() {
    return pageSession.get("uploading");
  }
});


Template.TEMPLATE_NAME.onRendered(function() {  
    $('#upload').fileinput({
        showCaption: false,
        showPreview: false,
        dropZoneEnabled: false,
      	allowedFileExtensions: ["txt", "csv"],
        showUpload: false,
        showRemove: false,
      //browseClass: "btn btn-primary",
        browseClass: "",
        browseLabel: "",
      //browseIcon: "<i class=\"fa fa-cloud-upload\" aria-hidden=\"true\" style=\"color:#666666\"></i>"
        browseIcon: ""
	});
});


Template.TEMPLATE_NAME.events({
   'change #upload': function(event, temp) {
      event.preventDefault();

     console.log('upload',event.target.files[0].name);
      bootbox.dialog({       	
			message: "Upload the file? Are you sure ?",
			title: "Upload",
			animate: false,
			buttons: {
				success: {
					label: "Yes",
					className: "btn-success",
					callback: function() {
						pageSession.set("uploading", true );
                      	console.log('p',event.target.files[0]);
					    Papa.parse( event.target.files[0], {
      					  header: true,
      					  complete( results, file ) {                            
        				    Meteor.call( 'parseUpload', event.target.files[0].name.split('.')[0], results.data, ( error, response ) => {
          					 if ( error ) {
            					console.log( error.reason );
          					   } else {
            					pageSession.set("uploading", false );
            					Bert.alert( 'Upload complete!', 'success', 'growl-top-right' );
          					 }
        				  });
      				    }
					});
				}
			},
			danger: {
				label: "No",
				className: "btn-default"
			}
		}
	  });
   },

  'dropped #dropzone': function(event, temp) {
   event.preventDefault();
   FS.Utility.eachFile(event, function(file) { 
     //console.log('file',file.name);
     bootbox.dialog({
			message: "Upload the file? Are you sure ?",
			title: "Upload",
			animate: false,
			buttons: {
				success: {
					label: "Yes",
					className: "btn-success",
					callback: function() {
              			pageSession.set("uploading", true );
					    Papa.parse( file.name, {
      					header: true,
      					complete( results, file ) {
        				Meteor.call( 'parseUpload', file.split('.')[0] , results.data, ( error, response ) => {
          					if ( error ) {
            					console.log( error.reason );
          					} else {
            					pageSession.set("uploading", false );
            					Bert.alert( 'Upload complete!', 'success', 'growl-top-right' );
												}
										});
									}
								});
							}
						},
					danger: {
						label: "No",
						className: "btn-default"
					}
				}
			});
		});
	}
});

