Template.TEMPLATE_NAME.helpers({

 "start": function(status){ 
			var val; 
            if (["Started","Working in progress","Closed","Approved","Archived"].indexOf(status) >= 0) { val = "selected"; }
			return val;
 },

 "wip":   function(status){
			var val; 
            if (["Working in progress","Closed","Approved","Archived"].indexOf(status) >= 0) { val = "selected"; }
			return val;
 },

 "close": function(status, nextreadyStatus) {
                var val;
                if (["Closed","Approved","Archived"].indexOf(status) >= 0) { val = "selected"; }
                if (nextreadyStatus && status == "Working in progress") { val = "suggested"; }
                return val;
 },
 "approve": function(status) {
            var val;
            if (["Approved","Archived"].indexOf(status) >= 0) { val = "selected"; }
            if (status == "Closed" && Users.isAdmin(Meteor.userId())) { val = "suggested"; }
            return val;
 },

 "archive": function(status) {
            var val;            
            if (status == "Approved") { val = "suggested"; }
			if (status == "Archived") { val = "selectedend";}
            return val;        
 }

});

