import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

Template.TEMPLATE_NAME.onRendered(function() {
 let editor;
 ClassicEditor
    .create( document.querySelector( '#editor' ) )
    .then( newEditor => {
		editor = newEditor;
		var data = Session.get('activity');
		if (data.memo) { editor.setData(data.memo) };
   		var save_timeout;
	    save_data = function() {
		   	clearTimeout(save_timeout);
			data.memo = editor.getData();
			if (data.memo !== Session.get('activity').memo)  {
			    Meteor.call("activitiesUpdate", Session.get('activity')._id, data, function(e, r){if(e){console.log('Error',e);}});
				Session.set('activity', data);
			};
		};
		editor.model.document.on( 'change:data', () => {
				//console.log( 'The data has changed!' );
				clearTimeout(save_timeout);
				save_timeout = setTimeout(save_data, 2000);
		})
	})
    .catch( error => {
        console.error( error );
  });
});

