import {ReactiveAggregate} from 'meteor/jcbernack:reactive-aggregate';

var os = Npm.require('os');
var containerized = require('containerized');
var fileMonitor= ['Branch','Libtest','Tests','Normative','Risks','Organizationunit','Users','Registrycontrols','Processes','Issues','Monitoringdetection','Activities'];

passwdAD ="";

var getIp = function() {
    // Get interfaces
    var netInterfaces = os.networkInterfaces();
    // Result
    var result = [];
    for (var id in netInterfaces) {
      var netFace = netInterfaces[id];

      for (var i = 0; i < netFace.length; i++) {
        var ip = netFace[i];
        if (ip.internal === false && ip.family === 'IPv4') {
          result.push(ip);
        }
      }
    }   
    return result[0].address;
};


