Template.registerHelper("showIcon", function (status) {
  var s="";  
  switch(status) {
       case "Started":
            s = "fa fa-hourglass-start";
            break;
        case "Working in progress":
            s = "fa fa-cogs";
            break;
        case "Closed":
            s = "fa fa-diamond";
            break;
        case "Approved":
            s = "fa fa-check";
            break;
        case "Archived":
            s = "fa fa-lock";
            break;
  } 
  var codehtml="";
  codehtml="<div title='"+status+"' style='text-align:center;'><i class='"+s+"'aria-hidden='true'></i></div>";  
  return new Handlebars.SafeString(codehtml);    
});

