/******************************************/
/*  */
/******************************************/
.wrapper {
  display: table;
  height: 100%;
  width: 100%;
}

.container-fostrap {
  display: table-cell;
  padding: 1em;
  text-align: center;
  vertical-align: middle;
}

.card {
    display: block;
  /* ---mtt */
    height: 100%;
    position: relative;
  /* ---mtt */
    margin-top: 20px;
    margin-bottom: 25px;
    line-height: 1.42857143;
    background-color: #fff;
    border-radius: 2px;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12);
    transition: box-shadow .25s;
}
.card:hover {
  box-shadow: 0 8px 17px 0 rgba(0,0,0,0.2),0 6px 20px 0 rgba(0,0,0,0.19);
}
.img-card {
  width: 100%;
  height:100%;
  border-top-left-radius:2px;
  border-top-right-radius:2px;
  display:block;
  overflow: hidden;
}

.img-card img{
  width: 100%;
  height: 200px;
  object-fit:cover;
  transition: all .25s ease;
}

.card-content {
  padding:10px;
  font-size: 0.9em;
  text-align:left;
  /* ---mtt */
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 8;
  -webkit-box-orient: vertical;
  /* ---mtt */
}
.card-title {
  padding-top:4px;
  padding-left:10px;
  padding-right:10px;
  height: 36px;
  text-align:left;
  font-size: 0.9em;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
}

.card-title a {
  color: #000;
  text-decoration: none !important;
}

.card-read-more {
    /* ---mtt */
  position: absolute;
  bottom: 0; right: 0;  left: 0;
    /* ---mtt */
 // border-top: 1px solid #D4D4D4;
}
.card-read-more a {
  //text-decoration: none !important;
  padding:2px;
//  font-weight:600;
  //text-transform: uppercase
}

/******************************************/




.dashboard {
  	border: #cdcdcd medium solid;
	border-radius: 10px;
    height: 100%;
  	font-size: small;
}

.no-border [class*="-6"] {
	padding-left:0;
    padding-right:0;
}

.divlib {
    border-style: solid;
    border-width: 2px;
  	border-radius: 25px;
  	font-size: small;
}


ul.list-group:after {
  content: "";
}


li.borderless {
  border: 0 none;
}

