	font-size: 16px;
}
*/

@keyframes statusicon {
    from { color: #fff;}
    to {color: #a6c167;}
}


@keyframes fonticon {
  0% {font-size: 15px;}
  10% {font-size: 17px;}
  20% {font-size: 19px;}
  30% {font-size: 21px;}
  40% {font-size: 23px;}
  60% {font-size: 25px;}
  70% {font-size: 27px;}
  80% {font-size: 28px;}
  90% {font-size: 29px;}
 100% {font-size: 15px;}
